  selectedPlan.forEach((day, index) => {
    // Create a day heading
    const dayHeading = document.createElement("h2");
    dayHeading.textContent = `Day ${index + 1} - ${day.workout}`;
    workoutPlanDisplay.appendChild(dayHeading);

