        exerciseItem.textContent = `${exercise.name}: ${exercise.sets} sets of ${exercise.reps} reps`;
      } else if (exercise.time) {
        exerciseItem.textContent = `${exercise.name}: ${exercise.sets || ""} for ${exercise.time}`;
      } else {
        exerciseItem.textContent = `${exercise.name}`;
      }
      exerciseList.appendChild(exerciseItem);
    });

    workoutPlanDisplay.appendChild(exerciseList);
  });
}
